mapper.select:
SELECT 

FROM am_sign a

WHERE a.id = #{id}

SELECT 

FROM am_sign a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_name LIKE 
			'%'||#{userName}||'%'
			'%'+#{userName}+'%'
			concat('%',#{userName},'%')


		AND a.sign_time BETWEEN #{beginSignTime} AND #{endSignTime}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM am_sign a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE am_sign SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
