mapper.select:
SELECT 

FROM am_sign a

WHERE a.id = #{id}

SELECT
FROM am_sign a
LEFT JOIN sys_office o on u2.office_id = o.id LEFT JOIN sys_office c on u2.company_id = c.id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.user_name LIKE '%'||#{userName}||'%' '%'+#{userName}+'%' concat('%',#{userName},'%')
AND a.sign_time >= #{beginSignTime}
AND a.sign_time => #{endSignTime}
AND (c.id = #{user.company.id} OR c.parent_ids LIKE '%,'||#{user.company.id}||',%') '%,'+#{user.company.id}+',%') CONCAT('%,', #{user.company.id}, ',%'))
AND (o.id = #{user.office.id} OR o.parent_ids LIKE '%,'||#{user.office.id}||',%') '%,'+#{user.office.id}+',%') CONCAT('%,', #{user.office.id}, ',%'))
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM am_sign a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
