mapper.select:
SELECT 

FROM sm_schedule a

WHERE a.id = #{id}

SELECT 

FROM sm_schedule a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.type_name = #{typeName}


		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sm_schedule a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sm_schedule SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
